var Microtext={};String.prototype.nameAt=function(pos){"use strict";var brokenNames=Microtext.breakSentence(this);return brokenNames[pos]};Microtext.breakSentence=function(sentence){"use strict";return sentence.trim().split(" ")};Microtext.truncate=function(sentence,length){"use strict";if(sentence.constructor===String&&length.constructor===Number){return sentence.slice(0,length)+"..."}};Microtext.firstName=function(names){"use strict";var brokenNames=Microtext.breakSentence(names);return brokenNames[0]};Microtext.lastName=function(sentence){"use strict";var brokenNames=Microtext.breakSentence(sentence);if(brokenNames.length>1){return brokenNames[brokenNames.length-1]}if(brokenNames.length===1){return Microtext.firstName(sentence)}};Microtext.getInitials=function(expr){"use strict";var brokenNames=Microtext.breakSentence(expr),abbrevName,nameList=[];brokenNames.forEach(function(name){nameList.push(name.charAt(0))});if(nameList.length>1){abbrevName=nameList.toString().toUpperCase();return abbrevName.replace(/\,/g,". ")}};Microtext.abbrevName=function(expr){"use strict";var brokenNames=Microtext.breakSentence(expr);if(brokenNames.length>1){return brokenNames[0]+" "+brokenNames[1].charAt(0)+"."}return brokenNames[0]};Microtext.capitalize=function(expr){"use strict";var capitalized;if(expr!==undefined){if(expr.constructor===String){capitalized=expr.charAt(0).toUpperCase()+expr.slice(1)}else{capitalized=expr.toString()}}return capitalized};Microtext.simpleFormat=function(expr,htmlOptions){"use strict";var options=htmlOptions||{},className=options.className,outputString;if(className!==undefined){outputString="<p class='"+className+"'>"+expr+"</p>";return outputString}return"<p>"+expr+"</p>"};Microtext.excerpt=function(expr,phrase){"use strict";var startPos=expr.indexOf(phrase),outputString;if(typeof startPos==="number"){return"..."+expr.substr(startPos)+"..."}outputString=expr;return outputString};Microtext.hideEmail=function(email){"use strict";var avg,splitted,firstPart,secondPart,finalStr;splitted=email.split("@");firstPart=splitted[0];avg=firstPart.length/2;firstPart=firstPart.substring(0,firstPart.length-avg);secondPart=splitted[1];finalStr=firstPart+"...@"+secondPart;return finalStr};Microtext.parameterize=function(string){"use strict";return string.trim().toLowerCase().replace(/[^a-zA-Z0-9 -]/,"").replace(/\s/g,"-")};Microtext.hideBadWord=function(word){"use strict";var i,rand,answer=word[0],replacements=["@","!","$","#","&","*"];for(i=1;i<word.length-1;i+=1){rand=Math.floor(Math.random()*replacements.length-1)+1;if(word.charAt(i)===" "){answer+=" "}else{answer+=replacements[rand]}}return answer};Microtext.ordinal=function(num){"use strict";var result=String(num);if([11, 12, 13].indexOf(num)!==-1){result+='th';}else{switch(result[result.length-1]){case'1':result+='st';break;case'2':result+='nd';break;case'3':result+='rd';break;default:result+='th';}}return result;};